    top: -40vh;
   }
  }
 }
 div.open {
  top: 2vh;
  //   transform: rotate(-360deg);
  //   animation: spin 0.1s linear;
  animation: movedown 0.1s linear;
  @keyframes movedown {
   from {
    opacity: 1;
    top: -40vh;
   }
   to {
    opacity: 1;
    top: 2vh;
   }
  }
 }

 .mobile-nav {
  //   border: 1px solid rgba(99, 99, 99, 0.658);
  display: none;
  padding: 0.8em;
  border-radius: 10%;
  background-color: rgb(1, 24, 1);
  position: absolute;
  right: 4%;
  top: 1em;
  cursor: pointer;
  //   color: whitesmoke;
  //   font-size: 3rem;
  //   @include flexCentered;
  flex-direction: column;
  transition: all 0.1s ease-in-out;
  > img {
   width: 2.6em;
   opacity: 1;
  }
  .line1 {
   animation: navdown 2s ease-in 3s infinite alternate;
   @keyframes navdown {
    0% {
     opacity: 0.6;
    }
    50% {
     opacity: 0.8;
    }
    100% {
     opacity: 1;
    }
   }
  }
  .line2 {
   animation: navdown 1s ease-in 2s infinite alternate;
   @keyframes navdown {
    0% {
     opacity: 0.6;
    }
    50% {
     opacity: 0.8;
    }
    100% {
     opacity: 1;
    }
   }
  }
  .line3 {
   animation: navdown 2s ease-in 1s infinite alternate;
   @keyframes navdown {
    0% {
     opacity: 0.6;
    }
    50% {
     opacity: 0.8;
    }
    100% {
     opacity: 1;
    }
   }
  }
  animation: movein 0.1s ease-in;
  @keyframes movein {
   from {
    right: -40vw;
   }
   to {
    right: 4%;
   }
  }
 }

 .mobile-nav.mobile-navMove {
  right: -40vw;
  animation: moveout 0.1s ease-out;
  @keyframes moveout {
   from {
    right: 4%;
   }
   to {
    right: -40vw;
   }
  }
 }

 @media screen and (max-width: 788px) {
  .mobile-nav {
   @include flexCentered;
  }
  ul > li {
   display: none;
  }
 }

 .activeNav {
  ul.nav-container__link-container {
   background: #030602fb;
   position: fixed;
   height: 100%;
   justify-content: space-evenly;
   flex-direction: column;
   width: 100%;
   top: 0;
   left: 0;

   & > li {
    display: inline-block;
   }
  }
 }
}

