.slide {
 @include flexCentered;
 align-items: flex-start;
 //  text-align: center;

 img.slide-techguy {
  width: 80%;
  height: 100vh;
 }

 &-primary {
  width: 70%;

  &__content-container {
   display: grid;
   grid-template-columns: 4fr 17em;
   position: absolute;
   top: 0;
   width: 70%;
   height: 78vh;
   @media screen and (max-width: 770px) {
    width: 100%;
   }
  }
  &__img-container {
   height: 100vh;
   text-align: center;
   display: flex;
   //    @include flexCentered;
  }

  &__img-cyberpunk {
   background: linear-gradient(
    180deg,
    rgba(31, 30, 30, 0.9) 46.25%,
    rgba(53, 53, 53, 0.892155) 78.69%,
    rgba(71, 71, 71, 0.98) 103.47%
   );
   width: 100%;
   img.slide-cyberpunk {
    width: 80%;
    height: 100vh;
   }
  }
  &__img-techguy {
   //    background: linear-gradient(
   //     180deg,
   //     rgba(31, 30, 30, 0.9) 46.25%,
   //     rgba(53, 53, 53, 0.892155) 78.69%,
   //     rgba(71, 71, 71, 0.98) 103.47%
   //    );
   position: absolute;
   right: -100vw;
   width: 100%;
   img.slide-techguy {
    width: 100%;
    height: 100vh;
   }
  }
  &__content-container__text {
   //    border: 1px solid blue;
   //    left: 2%;
   //   position: absolute;
   //   top: 16.6em;
   width: 75%;
   align-self: end;
   margin-left: 3em;
   margin-bottom: 7em;
  }

  &__side-content__container {
   display: inline-grid;
  }
  &__header {
   //   position: absolute;
   //   top: 3em;
   //   left: 2vw;
   font-size: 7vw;
   color: #79e2bd;
  }

  &__text-container {
   color: whitesmoke;
   //   position: fixed;
   //   top: 30vh;
   //   left: 3vw;
   line-height: 2.4em;
   display: flex;
   justify-content: space-between;
   //   width: 70%;
   //    border: $borderTest;
  }

  &__side-header {
   //   position: absolute;
   //   top: 35px;
   //   position: relative;
   //   right: 0;
   //   top: 1em;
   color: rgb(247, 247, 247);
   right: 29%;
   writing-mode: vertical-rl;
   //   font-size: 3rem;
   font-size: 2vw;
   position: relative;
   right: 10%;
   top: 1em;
   line-height: 30px;
   letter-spacing: -1px;
   &-date {
    position: relative;
    top: 4.9em;
   }
  }
 }

 p.slide-text-left,
 p.slide-text-right {
  width: 40%;
  height: 10em;
  font-size: 1.5rem;
  letter-spacing: 1px;
  font-weight: bold;
 }

 &__pagination {
  //   border: 1px solid aliceblue;

  background: rgb(10, 10, 10);
  @include flexCentered;
  align-self: end;
  font-size: 3rem;
  width: 100%;
  height: 2em;
  &-left,
  &-right {
   font-weight: bolder;
   color: whitesmoke;
   width: 2em;
   height: 2em;
   @include flexCentered;
   &:hover {
    background-color: rgb(36, 36, 36);
   }
   &:active {
    font-size: 3.1rem;
   }
  }
 }

 &-footer {
  display: grid;
  grid-template-columns: 2fr 2fr;
  height: 22vh;
  position: absolute;
  bottom: 0em;
  width: 70%;
 }
 &-footer__left {
  width: 100%;
  margin-left: 3em;

  &__btn-container {
   padding: 0.8em;
   display: flex;
  }
  &__play-btn {
   padding: 1em;
   background: #111;
   border-radius: 50%;
   display: flex;
   justify-content: center;
   align-content: center;
   align-items: center;
   cursor: pointer;
   &:hover {
    background: rgb(31, 31, 31);
   }
  }

  i.fas.fa-play {
   color: #e6e6e6;
   position: relative;
   left: 10%;
  }

  &__play-text {
   font-size: 1.5rem;
   padding: 0.4em;
   color: #e6e6e6;
  }

  &__social-media {
   font-size: 1.3rem;
   display: flex;
   position: relative;
   bottom: -4em;
   width: 88%;
   height: 6vh;

   ul {
    @include flexCentered;
    flex-wrap: wrap;

    li {
     margin: 0 0.4em;
    }
    li > a {
     color: white;
     padding: 0.3em;
     font-weight: bold;
     //  border: $borderTest;
     &:hover {
      color: $hoverColor;
     }
    }
   }
  }
 }

 &-footer__middle {
  //   width: 60em;
  display: flex;
  background: #f5f5f5;
  padding: 1em 1.8em;
  height: 22vh;

  &-pagenumber {
   font-size: 9rem;
   padding-top: 0.1em;
  }
  &-text {
   line-height: 1.8em;
   color: #1a1a1a;
   letter-spacing: 1px;
   letter-spacing: 1px;
   padding: 1em;
  }

  &-header {
   font-size: 2rem;
   margin-bottom: 0.5em;
   font-weight: bold;
  }

  &-text__text {
   //    font-size: 1.5rem;
   font-size: 1.7vh;
  }
 }

 @media screen and (max-width: 768px) {
  section.slide-primary {
   position: relative;
   z-index: 2;
  }
 }

 @media screen and (max-width: 1230px) {
  .slide-footer {
   grid-template-columns: 2fr 3fr;
  }
  .slide-primary__content-container {
   grid-template-columns: 4fr 1fr;
  }
  .slide-primary__content-container__text {
   width: 90%;
   margin-bottom: 13em;
  }
  .slide-primary__text-container {
   justify-content: center;
  }
  .slide-text-left {
   margin-right: 3em;
   margin-left: -4em;
  }
 }

 @media screen and (max-width: 1012px) {
  .slide-footer__middle-text {
   padding: 0.7em;
  }
 }
 @media screen and (max-width: 900px) {
  .slide-footer__middle {
   padding: 1em;
   &-text {
    padding: 0.5em;
   }
  }

  //    .slide .slide-footer__middle-text {
  //     padding: 0.5em;
  //    }
 }
}

// slide animation

.slide-primary__img-cyberpunk {
 //  transition: all 8s ease-in-out 8s;
 //  animation: slidePunk 6s ease-in-out 250s infinite alternate;
 animation-name: slidepunk;
 animation-iteration-count: infinite;
 animation-duration: 4s;
 animation-delay: 3s;
}
.slide-primary__img-techguy {
 transition: all 4s ease-in-out 2s;
 //  animation: primarySlide 4s ease-in-out 2s alternate;
}

@keyframes slidepunk {
 from {
  transform: translateX(0);
 }
 to {
  transform: translateX(-100vw);
 }
}

